$(document).ready(function(){$(".slideshow").each(function(s,a){var t="slideshow"+s;this.id=t,$(this).slick({slide:"#"+t+" .item",appendArrows:"#"+t+" .arrows",prevArrow:'<button type="button" class="slideshow-prev btn-link"><span class="fa-stack fa-lg"><i class="fa fa-circle fa-stack-2x"></i><i class="fa fa-angle-left fa-stack-1x"></i></span></button>',nextArrow:'<button type="button" class="slideshow-next btn-link"><span class="fa-stack fa-lg"><i class="fa fa-circle fa-stack-2x"></i><i class="fa fa-angle-right fa-stack-1x"></i></span></button>',autoplay:!0})}),$(".slideshow-prev, .slideshow-next").on("click",function(s){$(this).closest(".slideshow").slick("slickPause")})});